// PDF.js Worker Fallback
// This is a minimal worker implementation for PDF.js compatibility

if (typeof window === 'undefined') {
  // Node.js environment - not supported
  throw new Error('PDF.js worker requires browser environment')
}

// Simple worker implementation for PDF.js
self.onmessage = function(e) {
  // Basic worker message handling
  const { data } = e
  
  if (data.type === 'render') {
    // Simulate PDF rendering work
    setTimeout(() => {
      self.postMessage({
        type: 'renderComplete',
        data: { success: true }
      })
    }, 100)
  }
}

// Export for module compatibility
if (typeof module !== 'undefined' && module.exports) {
  module.exports = self
}